import{L as a}from"./chunk-P372ZLAE.js";var s=(e=>(e.VERBS="Verben",e.ARTICLES="Artikel",e.PRONOUNS="Pronomen",e.SENTENCE_STRUCTURE="Satzstellung",e.PREPOSITIONS="Pr\xE4positionen",e.COMPARISON="Vergleich",e))(s||{});var t=class r{grammarRules=[{id:1,title:"Pr\xE4sens (Gegenwart)",description:"Im Schweizerdeutschen wird das Pr\xE4sens \xE4hnlich wie im Hochdeutschen gebildet, jedoch mit leichten Abweichungen in der Aussprache und teilweise in den Endungen.",examples:[{germanPhrase:"Ich gehe nach Hause",swissGermanPhrase:"Ich gang hei",explanation:'Das Verb "gehen" wird zu "gang" und "nach Hause" zu "hei"'},{germanPhrase:"Du hast ein Buch",swissGermanPhrase:"Du hesch es Buech",explanation:'Das Verb "haben" wird zu "hesch" und "ein Buch" zu "es Buech"'},{germanPhrase:"Wir essen Brot",swissGermanPhrase:"Mir \xE4ssed Brot",explanation:'Das Verb "essen" wird zu "\xE4ssed" und "wir" zu "mir"'}],category:"Verben"},{id:2,title:"Personalpronomen",description:"Die Personalpronomen im Schweizerdeutschen unterscheiden sich vom Hochdeutschen und k\xF6nnen je nach Region leicht variieren.",examples:[{germanPhrase:"ich - du - er/sie/es - wir - ihr - sie",swissGermanPhrase:"ich - du - er/si/es - mir - ihr - si",explanation:'Beachte besonders "mir" statt "wir" und "si" statt "sie"'},{germanPhrase:"Wir gehen ins Kino",swissGermanPhrase:"Mir g\xF6nd is Kino",explanation:'"Wir" wird zu "mir" und "gehen" zu "g\xF6nd"'}],category:"Pronomen"},{id:3,title:"Verben im Infinitiv",description:'Infinitive enden im Schweizerdeutschen oft auf "-e" statt auf "-en" wie im Hochdeutschen.',examples:[{germanPhrase:"gehen, machen, kommen",swissGermanPhrase:"gah, mache, cho",explanation:"Beachte die unterschiedlichen Endungen und teilweise komplett andere Formen"},{germanPhrase:"Ich will schlafen",swissGermanPhrase:"Ich will schlafe",explanation:'Der Infinitiv "schlafen" wird zu "schlafe"'}],category:"Verben"},{id:4,title:"Bestimmte und Unbestimmte Artikel",description:"Artikel unterscheiden sich im Schweizerdeutschen vom Hochdeutschen und folgen anderen Regeln.",examples:[{germanPhrase:"der, die, das - ein, eine",swissGermanPhrase:"de, d, s - en, e, es",explanation:'Unbestimmte Artikel variieren je nach Geschlecht: "en" (m), "e" (f), "es" (n)'},{germanPhrase:"Das ist ein Haus",swissGermanPhrase:"Das isch es Huus",explanation:'Der unbestimmte Artikel f\xFCr Neutrum ist "es" statt "ein"'}],category:"Artikel"},{id:5,title:"Satzstellung bei Fragen",description:"Die Satzstellung bei Fragen im Schweizerdeutschen folgt \xE4hnlichen Regeln wie im Hochdeutschen, mit einigen Besonderheiten.",examples:[{germanPhrase:"Gehst du nach Hause?",swissGermanPhrase:"Gasch du hei?",explanation:"Das Verb am Anfang, dann das Subjekt"},{germanPhrase:"Was machst du heute?",swissGermanPhrase:"Was machsch du h\xFCt?",explanation:"Fragewort, dann Verb, dann Subjekt"}],category:"Satzstellung"},{id:6,title:"Vergangenheit (Perfekt)",description:"Im Schweizerdeutschen wird haupts\xE4chlich das Perfekt f\xFCr die Vergangenheit verwendet, das Pr\xE4teritum existiert praktisch nicht.",examples:[{germanPhrase:"Ich habe gegessen",swissGermanPhrase:"Ich han g\xE4sse",explanation:'"habe" wird zu "han" und "gegessen" zu "g\xE4sse"'},{germanPhrase:"Wir sind gegangen",swissGermanPhrase:"Mir sind gange",explanation:'"Wir" wird zu "mir" und "gegangen" zu "gange"'}],category:"Verben"},{id:7,title:"Diminutive (Verkleinerungsformen)",description:'Im Schweizerdeutschen werden Diminutive h\xE4ufig mit "-li" gebildet und viel h\xE4ufiger verwendet als im Hochdeutschen.',examples:[{germanPhrase:"Haus \u2192 H\xE4uschen",swissGermanPhrase:"Huus \u2192 H\xFC\xFCsli",explanation:'Die Verkleinerungsform wird mit "-li" gebildet'},{germanPhrase:"Kind \u2192 Kindchen",swissGermanPhrase:"Chind \u2192 Chindli",explanation:'Auch hier wird die Endung "-li" verwendet'}],category:"Vergleich"},{id:8,title:"Pr\xE4positionen mit Ort und Richtung",description:"Pr\xE4positionen im Schweizerdeutschen folgen anderen Regeln als im Hochdeutschen, besonders bei Ortsangaben.",examples:[{germanPhrase:"in die Stadt gehen",swissGermanPhrase:"i d Stadt gah",explanation:'"in die" wird verk\xFCrzt zu "i d"'},{germanPhrase:"auf dem Tisch",swissGermanPhrase:"ufem Tisch",explanation:'"auf dem" wird zusammengezogen zu "ufem"'}],category:"Pr\xE4positionen"}];constructor(){}getAllGrammarRules(){return[...this.grammarRules]}getGrammarRulesByCategory(i){return this.grammarRules.filter(n=>n.category===i)}getGrammarCategories(){return Object.values(s)}getGrammarRuleById(i){return this.grammarRules.find(n=>n.id===i)}static \u0275fac=function(n){return new(n||r)};static \u0275prov=a({token:r,factory:r.\u0275fac,providedIn:"root"})};export{t as a};
